(180, '_transient_receptar-custom-css-debug', '/**\r\n * Theme custom styles\r\n *\r\n * @package    Receptar\r\n * @copyright  2015 WebMan - Oliver Juhas\r\n *\r\n * @since    1.0\r\n * @version  1.0\r\n */\r\n\r\n\r\n\r\n	/* Background color */\r\n\r\n		body { background-color: #f5f7f9; }\r\n\r\n	/* Header color */\r\n\r\n		.site-header {\r\n			background-color: rgba(0,0,0,0.2);\r\n			color: #ffffff;\r\n		}\r\n\r\n		.not-scrolled.is-posts-list .site-header,\r\n		.not-scrolled.paged .site-header {\r\n			background-color: #2a2c2e;\r\n			color: #ffffff;\r\n		}\r\n\r\n	/* Sidebar color */\r\n\r\n		.secondary {\r\n			background-color: #1a1c1e;\r\n			color: #9a9c9e;\r\n			border-color: #3a3c3e;\r\n		}\r\n\r\n		.secondary-controls,\r\n		.secondary h1,\r\n		.secondary h2,\r\n		.secondary h3,\r\n		.secondary h4,\r\n		.secondary h5,\r\n		.secondary h6,\r\n		.current-menu-item > a { color: #ffffff; }\r\n\r\n		.hamburger-item { background-color: #ffffff; }\r\n\r\n		.main-navigation a:hover,\r\n		.current-menu-item > a { background-color: #3a3c3e; }\r\n\r\n	/* Content color */\r\n\r\n		body,\r\n		code { color: #6a6c6e; }\r\n\r\n		h1, h2, h3, h4, h5, h6,\r\n		.h1, .h2, .h3, .h4, .h5, .h6 { color: #1a1c1e; }\r\n\r\n		.site,\r\n		.home .content-area,\r\n		.posts .hentry { background-color: #ffffff; }\r\n\r\n		.site-content { border-color: #eaecee; }\r\n\r\n		hr,\r\n		code,\r\n		pre,\r\n		.entry-meta-bottom { background-color: #eaecee; }\r\n\r\n		/* Alternative colors */\r\n\r\n			.site-content .pagination,\r\n			.site-content .comments-area-wrapper {\r\n				background-color: #2a2c2e;\r\n				color: #9a9c9e;\r\n				border-color: #3a3c3e;\r\n			}\r\n\r\n			.posts,\r\n			.posts .entry-inner:after { background-color: #2a2c2e; }\r\n\r\n			.comments-area-wrapper h1,\r\n			.comments-area-wrapper h2,\r\n			.comments-area-wrapper h3,\r\n			.comments-area-wrapper h4,\r\n			.comments-area-wrapper h5,\r\n			.comments-area-wrapper h6 { color: #ffffff; }\r\n\r\n	/* Footer color */\r\n\r\n		.site-footer {\r\n			background-color: #f5f7f9;\r\n			color: #9a9c9e;\r\n		}\r\n\r\n	/* Accent color and accent text color */\r\n\r\n		a,\r\n		.accent-color { color: #e53739; }\r\n\r\n		mark,\r\n		ins,\r\n		.highlight,\r\n		pre:before,\r\n		.pagination .current,\r\n		.button,\r\n		button,\r\n		form button,\r\n		.fl-node-content button,\r\n		input[type=\"button\"],\r\n		input[type=\"reset\"],\r\n		input[type=\"submit\"],\r\n		.post-navigation .post-title,\r\n		.bypostauthor > .comment-body .comment-author:before,\r\n		.comment-navigation a,\r\n		.widget_calendar tbody a,\r\n		.widget .tagcloud a:hover,\r\n		body #infinite-handle span,\r\n		.site-content div.sharedaddy .sd-content ul li a.sd-button:not(.no-text) {\r\n			background-color: #e53739;\r\n			color: #ffffff;\r\n		}\r\n\r\n			.site-content div.sharedaddy .sd-content ul li a.sd-button:not(.no-text) { color: #ffffff !important; }\r\n\r\n			.infinite-loader .spinner > div > div { background: #e53739 !important; }\r\n\r\n		input:focus,\r\n		select:focus,\r\n		textarea:focus,\r\n		.post-navigation .post-title,\r\n		.widget .tagcloud a:hover { border-color: #e53739; }\r\n\r\n		mark,\r\n		ins,\r\n		.highlight {\r\n			-webkit-box-shadow: .38em 0 0 #e53739, -.38em 0 0 #e53739;\r\n			        box-shadow: .38em 0 0 #e53739, -.38em 0 0 #e53739;\r\n		}\r\n\r\n\r\n\r\n/**\r\n * Responsive color setup\r\n */\r\n\r\n	@media only screen and (max-width: 960px) {\r\n\r\n		.site-header { background-color: #2a2c2e; }\r\n\r\n	} /* /(max-width: 960px) */\r\n\r\n\r\n\r\n/* End of file */', 'yes'),
